script:
frappe.ui.form.on('Employee', {
    employment_type(frm) {
        if (['Joining', 'Probation', 'Confirmation'].includes(frm.doc.employment_type)) {
            frm.set_value('status', 'Active');
        } else if (frm.doc.employment_type === 'Exit') {
            frm.set_value('status', 'Left');
        }
    },
    refresh(frm) {
        if (['Joining', 'Probation', 'Confirmation'].includes(frm.doc.employment_type)) {
            frm.set_value('status', 'Active');
        } else if (frm.doc.employment_type === 'Exit') {
            frm.set_value('status', 'Left');
        }
    }
});
